            <header>
                <Nav className="nav-container">
                    <Nav.Item>
                        <Nav.Link href="/shop">SHOP</Nav.Link>
                    </Nav.Item>
                    <Nav.Item>
                        <NavDropdown title="SERVICES" id="basic-nav-dropdown" onMouseEnter={toggleServices}>
